test_that("include_file_image", {
  expect_equal(insert_base64(name = read_example("text.png")), "<file name=\"text.png\" encoding=\"base64\"></file>")

  expect_equal(insert_png(name = read_example("text.png")), "<img src=\"@@PLUGINFILE@@/text.png\" alt=\"\" role=\"presentation\"class=\"img-responsive atto_image_button_text-bottom\" width=\"111\" height=\"56\"><br>")

  expect_equal(read_base64_html(name = read_example("Graphics.html")), list(""))
})
